let rawOutput = items[0].json.output;


rawOutput = rawOutput.replace(/```json\s*|\s*```/g, '');  
rawOutput = rawOutput.replace(/^json\s*/, '');           


let parsed = {};
try {
  const firstParse = JSON.parse(rawOutput);
  if (typeof firstParse === 'string') {
    parsed = JSON.parse(firstParse);
  } else {
    parsed = firstParse;
  }
} catch (err) {
  throw new Error('Failed to parse AI Agent output as JSON: ' + err.message);
}


return [
  {
    json: {
      labels: parsed.labels ?? [],
      values: parsed.values ?? []
    }
  }
];

const labels = $('Clean Data').first().json.labels;
const values = $('Clean Data').first().json.values;

// Ensure arrays are valid
const cleanedLabels = labels.map(l => String(l));
const cleanedValues = values.map(v => Number(v));

// Configs:
const configLine = {
  type: 'line',
  data: {
    labels: cleanedLabels,
    datasets: [{
      label: 'Amount',
      data: cleanedValues,
      borderWidth: 3,
      pointStyle: 'circle'
    }]
  },
  options: {
    scales: {
      y: { beginAtZero: true }
    }
  }
};

const configBar = {
  type: 'bar',
  data: {
    labels: cleanedLabels,
    datasets: [{
      label: 'Amount',
      data: cleanedValues,
      borderWidth: 3
    }]
  },
  options: {
    scales: {
      y: { beginAtZero: true }
    }
  }
};

const configPie = {
  type: 'pie',
  data: {
    labels: cleanedLabels,
    datasets: [{
      label: 'Amount',
      data: cleanedValues
    }]
  }
};

// Encode URLs
const chartLineUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(configLine))}`;
const chartBarUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(configBar))}`;
const chartPieUrl = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(configPie))}`;

// Return result
return [{
  json: {
    ...items[0].json,
    chartLineUrl,
    chartBarUrl,
    chartPieUrl
  }
}];
